import React, { useState } from 'react'
import { Image, Text, TextInput, Button, View, StyleSheet, ActivityIndicator, } from 'react-native'
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';
import image from './../../../assets/splash.png'
import { firebase} from '../../config';
import { FontAwesome, MaterialCommunityIcons, AntDesign, FontAwesome5, } from '@expo/vector-icons';

export default function RegistrationScreen({navigation}) {
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')

    const onFooterLinkPress = () => {
        navigation.navigate('SigninScreen')
    }
    const onRegisterPress = () => {
        if (password !== confirmPassword) {
            alert("Passwords don't match.")
            return
        }
        firebase
            .auth()
            .createUserWithEmailAndPassword(email, password)
            .then((response) => {
                const uid = response.user.uid
                const data = {
                    id: uid,
                    email,
                    firstName,
                    lastName,
                };
                const usersRef = firebase.firestore().collection('users')
                usersRef
                    .doc(uid)
                    .set(data)
                    .then(() => {
                        navigation.navigate('HomeScreen', {user: data})
                    })
                    .catch((error) => {
                        alert(error)
                    });
            })
            .catch((error) => {
                alert(error)
    })
} 

 return (
        <View style={styles.container}>
            <KeyboardAwareScrollView
                style={{ flex: 1, width: '100%' }}
                keyboardShouldPersistTaps="always">
                    <Image style={styles.image}
                       source={ image }
                       PlaceholderContent={<ActivityIndicator size='large' />}
                    />
                <View style={styles.fbbutton}>
                <FontAwesome.Button 
                name="facebook"
                backgroundColor="#3b5998"
                size={24}
                borderRadius={20}
                style={styles.fbicon}


                >
                     Login with facebook        
                </FontAwesome.Button>
                </View>
                <View style={styles.gmailbutton} >
                    <MaterialCommunityIcons.Button
                     name="gmail" 
                     size={24} 
                     backgroundColor="#B23121" 
                     borderRadius={20}
                     style={styles.gmicon}
                     >
                    Login With G-Mail
                    </MaterialCommunityIcons.Button>
                
                </View>
                <View style={styles.footerView}>
                    <Text style={styles.footerText}>Already got an account? <Text onPress={onFooterLinkPress} style={styles.footerLink}>Log in</Text></Text>
                </View>
            </KeyboardAwareScrollView>
        </View>
  )}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    image:{
        marginTop:100,
        height: 200,
        width: 225,
        alignSelf: "center",
        margin: 30,
        marginBottom: -10
    },
    account: {
       alignItems:'center',
       marginLeft: 40,
       marginRight:40,
       marginBottom:10,
       backgroundColor:"lightgreen",
    },
    gmailbutton:{
       marginLeft:30,
       marginRight:30,
       marginTop:10,
       alignItems:'center',
       borderRadius:10

    },
    fbbutton:{
       marginTop:50,
       marginLeft:30,
       marginRight:30,
       alignItems:'center',
       borderRadius:40,
    },
    fbicon:{
        borderRadius:30,
        width: 300,
        height:50

    },
    gmicon:{
        borderRadius:30,
        width: 300,
        height:50

    },
    buttonTitle: {
        color: 'white',
        fontSize: 16,
        fontWeight: "bold"
    },
    footerView: {
        flex: 1,
        alignItems: "center",
        marginTop: 20
    },
    footerText: {
        fontSize: 16,
        color: '#2e2e2d'
    },
    footerLink:{
        color: "#788eec",
        fontWeight: "bold",
        fontSize: 16
    }
  });
