import React from 'react'
import {View, Text, StyleSheet, TouchableHighlight} from 'react-native'
import Icon from 'react-native-vector-icons/Ionicons';
import { connect }  from 'react-redux';
import { withNavigation } from 'react-navigation';
class AddCart extends React.Component {
    render () {
        return(
            <TouchableHighlight onPress={this.props.addItemToCart}>
            <View style={styles.Cartview}>
            <Text style={styles.Cartbutton}>ADD TO BASKET <Icon name="ios-add-circle" size={14} color='#fff'/> </Text>
            </View>
           </TouchableHighlight>
        )
    }
}
const mapDispatchToProps = (dispatch) =>{
    return{
      addItemToCart : (product) => dispatch({
        type:"ADD_TO_CART",
        payload:product
      })
    }
  }
  export default connect(mapDispatchToProps, null)(AddCart)

  const styles =StyleSheet.create({
    Cartbutton:{
        fontSize: 12,
        backgroundColor: '#8A173F',
        color:'#fff',
        alignItems:'center',
        justifyContent:'center'
      },
      Cartview:{
          marginBottom:8,
          marginRight:210,
          borderRadius:10,
          alignContent: 'center',
          alignItems:'center'
      }
  })
